var slider={_speed:400,_index:0,slides:{heroes:{title:"Heroes of the Storm Business Cards",text:"Heroes of the Storm themed business cards I created for TBKzord.",slides:["heroes-1.png","heroes-2.png","heroes-3.png"]}}};slider.next=function(){slider._index++;if(slider._index>=slider._selected.slides.length)slider._index=0;slider.update()};slider.previous=function(){slider._index--;if(slider._index<0)slider._index=slider._selected.slides.length-1;slider.update()};slider.update=function(){var e=slider._container;var i=slider._loader;var s=slider._selected;var d="images/work/"+s.slides[slider._index];e.stop().fadeOut(slider._speed,function(){i.attr("src",d).on("load",function(){e.css("background-image","url("+d+")").fadeIn(slider._speed)})})};slider.updateButtons=function(){var e=slider._selected.slides;if(e.length>1)slider._arrowButtons.show();else slider._arrowButtons.hide()};slider.open=function(e){var i=slider.slides[e];if(i){slider._selected=i;slider._index=0;slider._title.text(i.title);slider._info.text(i.text);slider.updateButtons();slider.update();slider._element.fadeIn()}};slider.close=function(){slider._element.fadeOut()};$(function(){$(".link").on("click",function(){window.location=$(this).attr("data-link")});$(".tile").on("click",function(){slider.open($(this).attr("data-slide"))});$("#slide-close").on("click",slider.close);var e=$("#slide-left").on("click",slider.previous);var i=$("#slide-right").on("click",slider.next);slider._arrowButtons=e.add(i);slider._element=$("#slide-cover");slider._container=$("#slide-image").on("click",slider.close);slider._loader=$("<img/>").appendTo(slider._container);slider._title=$("#slide-title");slider._info=$("#slide-info")});